$.fn.hideReveal = function(options) {
    options = $.extend({
        filter: '*',
        hiddenStyle: {
            opacity: 0.2
        },
        visibleStyle: {
            opacity: 1
        },
    }, options);
    this.each(function() {
        var $items = $(this).children();
        var $visible = $items.filter(options.filter);
        var $hidden = $items.not(options.filter);
        // reveal visible
        $visible.animate(options.visibleStyle);
        // hide hidden
        $hidden.animate(options.hiddenStyle);
    });
};

